sys_script_include.script:
function GetApps()
{
	var Clone = x_snc_devtools.Clone;
	var GetCleanAppName = x_snc_devtools.GetCleanAppName;

	var aApps = [];

	var grApps = new GlideRecord('sys_app');
	grApps.orderBy('name');
	grApps.query();
	while (grApps.next())
	{
		var app = Clone(grApps);
		app.name_clean = GetCleanAppName(app.name);
		aApps.push(app);
	}

	return aApps;
}
